import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './containers/public/signin/SignIn';
import Register from './containers/public/signup/SignUp';
import { NotesContainer } from './containers/Dashboard';
import Navbar from './components/navbar/Navbar';
import { Nomatch } from './containers/no-match/Nomatch';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('accessToken') !== '');

  const handleLogin = () => {
    // Simulate successful login (set accessToken in localStorage)
    localStorage.setItem('accessToken', 'example_access_token');
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    // Simulate logout (clear accessToken from localStorage)
    localStorage.removeItem('accessToken');
    setIsLoggedIn(false);
  };

  const PrivateRoute = ({ element, ...rest }) => {
    return isLoggedIn ? (
      <Route {...rest} element={element} />
    ) : (
      <Navigate to="/" replace />
    );
  };

  return (
    <Router>
      <div>
        <Navbar isLoggedIn={isLoggedIn} onLogout={handleLogout} />
        <Routes>
          <Route path="/" element={<Home onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
          {/* Protected route for /notes - requires authentication */}
          <PrivateRoute path="/notes/*" element={<NotesContainer />} />
          {/* Route for handling 404 */}
          <Route path="/404" element={<Nomatch />} />
          {/* Redirect any other unknown routes to 404 */}
          <Route path="*" element={<Navigate to="/404" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
