import React, { useState, useEffect } from 'react';
import { getMessages } from '../services/messageService';
import { Paper } from '@mui/material';


export default function Messages(props) {
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        // Fetch messages when props.noteId changes
        const fetchMessages = async () => {
            try {
                const data = await getMessages(props.noteId);
                setMessages(data);  // Assuming getMessages returns an array of messages
            } catch (error) {
                console.error('Error fetching messages:', error);
                // Optionally, handle error state or display a message to the user
            }
        };

        fetchMessages();
    }, [props.noteId]);  // Run effect whenever props.noteId changes
    console.log(messages)
    // Render messages or loading indicator
    return (
        <div>
            <div style={{height:"78.5vh"}}>
            <div  style={{backgroundColor: "#AAA9AA",height:"60px"}}><h4>Messages for Note ID: {props.noteId}</h4></div>
            {messages.length > 0 ? (
                <div style={{ maxHeight: "78.5vh", overflow: "auto" }}>
                    {messages.map((message, index) => (
                        <Paper key={index} style={{ margin: "50px", minHeight: "100px", maxWidth: "400px", wordWrap: "break-word",backgroundColor: "#f9f9f9", border: "1px solid #ccc" }}>
                            <h6>{message.message_content}</h6>
                        </Paper>
                    ))}
                </div>
            ) : (
                <p>Loading messages...</p>
            )}
            <div style={{backgroundColor: "#AAA9AA",height:"60px"}}>
                <input type="text"></input>
            </div>
            </div>
        </div>
    );

}
