import React, { createContext, useContext, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    const signIn = async (formData) => {
        const { email, password } = formData;

        try {
            const response = await axios.post('http://localhost:5000/login', {
                email,
                password
            });

            const userData = response.data.user; // Assuming backend returns user data
            setUser(userData); // Set user data in context

            // Navigate to '/notes' on successful login
            navigate('/notes');

            return userData;
        } catch (error) {
            console.error('Login error:', error);
            throw error; // Handle login error
        }
    };

    const signOut = () => {
        setUser(null);
        localStorage.removeItem('userEmail'); // Remove user email from localStorage
    };

    return (
        <AuthContext.Provider value={{ user, signIn, signOut }}>
            {children}
        </AuthContext.Provider>
    );
};
