import { createStore, applyMiddleware, combineReducers } from 'redux';
import thunk from 'redux-thunk'; // You can use redux-thunk for async actions if needed
import notesReducer from '/home/quanteon/notebook1/notebook-ui/src/store/reducers/notesReducer.js'; // Adjust path as per your project structure

// Combine reducers if you have multiple reducers
const rootReducer = combineReducers({
  notes: notesReducer,
});

// Create Redux store
const store = createStore(rootReducer, applyMiddleware(thunk)); // Apply middleware as needed (e.g., redux-thunk)

export default store;
