import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import FormDialog from '../form_dialog/FormDialog';
import Search from '../search/Search';
import { Paper } from '@mui/material';
import ClearIcon from '@mui/icons-material/Clear';

const Notes = () => {
  const [noteTitles, setNoteTitles] = useState([]);
  const [filteredNoteTitles, setFilteredNoteTitles] = useState([]);
  const [openDialog, setOpenDialog] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://localhost:5000/notes');
      const data = response.data;
      setNoteTitles(data); // Set initial note titles from API response
      setFilteredNoteTitles(data); // Initialize filtered notes with all notes on component mount
    } catch (error) {
      console.error('Error fetching notes:', error);
      // Handle error state or retry logic here
    }
  };

  const addNote = (title) => {
    setNoteTitles([...noteTitles, { note_title: title }]); // Assuming note structure matches your API response
  };

  const filterNotes = (searchValue) => {
    const filteredTitles = noteTitles.filter(note =>
      note.note_title.toLowerCase().includes(searchValue.toLowerCase())
    );
    setFilteredNoteTitles(filteredTitles);
  };

  const deleteNote = (noteId) => {
    fetch(`http://localhost:5000/notes/${noteId}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        console.log('Note deleted successfully from backend');
        // Update state after successful deletion
        const updatedNotes = noteTitles.filter(note => note.note_id !== noteId);
        setNoteTitles(updatedNotes);
        setFilteredNoteTitles(updatedNotes); // Update filtered notes if needed
      })
      .catch(error => {
        console.error('Error deleting note from backend:', error);
      });
  };


  return (
    <>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <h1>Notes</h1>
        <Fab color="primary" aria-label="add" onClick={() => setOpenDialog(true)}>
          <AddIcon />
        </Fab>
      </div>
      <Search filterNotes={filterNotes} />
      {filteredNoteTitles.map((note, index) => (
        <Paper key={note.note_id} elevation={10} style={{ width: "28vw", height: "6vh", margin: "10px", padding: "20px", display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <Link to={`${note.note_id}`} style={{ textDecoration: "none" }}><h2>{note.note_title}</h2></Link>
          <ClearIcon onClick={() => deleteNote(note.note_id)} style={{ cursor: "pointer" }} />
        </Paper>
      ))}
      <FormDialog open={openDialog} handleClose={() => setOpenDialog(false)} addNote={addNote} />
    </>
  );
};

export default Notes;
