import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './containers/public/signin/SignIn';
import Register from './containers/public/signup/SignUp';
import { NotesContainer } from './containers/Dashboard';
import Navbar from './components/navbar/Navbar';
import { Nomatch } from './containers/no-match/Nomatch';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(localStorage.getItem('accessToken') !== '');

  const handleLogin = () => {
    // Logic to handle successful login (set accessToken in localStorage)
    localStorage.setItem('accessToken', 'your_access_token_here');
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    // Logic to handle logout (clear accessToken from localStorage)
    localStorage.removeItem('accessToken');
    setIsLoggedIn(false);
  };

  return (
    <Router>
      <div>
        <Navbar isLoggedIn={isLoggedIn} onLogout={handleLogout} />
        <Routes>
          <Route path="/" element={<Home onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
          {/* Protected route for /notes - requires authentication */}
          {isLoggedIn ? (
            <Route path="/notes/*" element={<NotesContainer />} />
          ) : (
            <Route path="/" element={<Home onLogin={handleLogin} />} />
          )}
          {/* Route for handling 404 */}
          <Route path="/404" element={<Nomatch />} />
          {/* Redirect any other unknown routes to 404 */}
          <Route path="*" element={<Nomatch />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
