import React, { useState,useEffect } from 'react';
import { Link } from 'react-router-dom';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import FormDialog from '../form_dialog/FormDialog';
import Search from '../search/Search';
import { Paper } from '@mui/material';

const Notes = () => {
  const [openDialog, setOpenDialog] = useState(false);
  const [noteTitles, setNoteTitles] = useState(["note1", "note2", "note3"]);

  const handleOpenDialog = () => {
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
  };

  // Function to add a new note title to the state
  const addNote = (title) => {
    setNoteTitles([...noteTitles, title]);
  };
  const [viewportHeight, setViewportHeight] = useState(window.innerHeight);

  useEffect(() => {
    const handleResize = () => {
      setViewportHeight(window.innerHeight);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const paperstyle = { width: "28vw", height: "6vh", margin: "10px", padding: "20px" };

  return (
    <>
      <div style={{ display: "flex", justifyContent: "space-between" }}>
        <h1>Notes</h1>
        <Fab color="primary" aria-label="add" onClick={handleOpenDialog}>
          <AddIcon />
        </Fab>
      </div>
      <Search />
      <div style={{ height: viewportHeight * 0.78, overflowY: "scroll" }}>
      {noteTitles.map((title, index) => (
        <Link key={index} to={`/${index}`}>
          <Paper elevation={10} style={{ ...paperstyle }}>
            <h2>{title}</h2>
          </Paper>
        </Link>
      ))}
    </div>

      <FormDialog open={openDialog} handleClose={handleCloseDialog} addNote={addNote} />
    </>
  );
};

export default Notes;
