// LocalizationContext.js

import React, { createContext, useContext, useState } from 'react';
import en from './en.json'; // English translations
import fr from './fr.json'; // French translations

const LocalizationContext = createContext();

export const useLocalization = () => {
  const context = useContext(LocalizationContext);
  if (!context) {
    throw new Error('useLocalization must be used within a LocalizationProvider');
  }
  return context;
};

export const LocalizationProvider = ({ children }) => {
  const [locale, setLocale] = useState('en'); // Default to English

  const translations = {
    en,
    fr
    // Add more languages as needed
  };

  const changeLocale = (newLocale) => {
    setLocale(newLocale);
  };

  return (
    <LocalizationContext.Provider value={{ locale, translations, changeLocale }}>
      {children}
    </LocalizationContext.Provider>
  );
};
