import React, { useState, useEffect } from 'react';
import Fab from '@mui/material/Fab';
import AddIcon from '@mui/icons-material/Add';
import Paper from '@mui/material/Paper';

const Notes = () => {
  const [noteTitles, setNoteTitles] = useState(["note1", "note2", "note3"]);

  // Dynamically calculate viewport height
  const [viewportHeight, setViewportHeight] = useState(window.innerHeight);

  useEffect(() => {
    const handleResize = () => {
      setViewportHeight(window.innerHeight);
    };
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const paperstyle = { width: "28vw", height: "6vh", margin: "10px", padding: "20px" };

  return (
    <div style={{ height: viewportHeight * 0.78, overflowY: "scroll" }}>
      {noteTitles.map((title, index) => (
        <Paper key={index} elevation={10} style={{ ...paperstyle }}>
          <h2>{title}</h2>
        </Paper>
      ))}
    </div>
  );
};

export default Notes;
