import React, { useState, useEffect } from 'react';
import { getMessages } from '../services/messageService';
import { Paper } from '@mui/material';


export default function Messages(props) {
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        // Fetch messages when props.noteId changes
        const fetchMessages = async () => {
            try {
                const data = await getMessages(props.noteId);
                setMessages(data);  // Assuming getMessages returns an array of messages
            } catch (error) {
                console.error('Error fetching messages:', error);
                // Optionally, handle error state or display a message to the user
            }
        };

        fetchMessages();
    }, [props.noteId]);  // Run effect whenever props.noteId changes
    console.log(messages)
    // Render messages or loading indicator
    return (
        <div style={{ display: "flex", flexDirection: "column", minHeight: "100vh" }}>
            <div style={{ backgroundColor: "#AAA9AA", height: "60px", display: "flex", alignItems: "center", justifyContent: "center" }}>
                <h4>Messages for Note ID: {props.noteId}</h4>
            </div>

            <div style={{ flex: "1", overflow: "auto", padding: "10px" }}>
                {messages.length > 0 ? (
                    messages.map((message, index) => (
                        <Paper key={index} style={{ margin: "10px 0", padding: "10px", backgroundColor: "#f9f9f9", border: "1px solid #ccc" }}>
                            <h6 style={{ margin: "0", wordWrap: "break-word" }}>{message.message_content}</h6>
                        </Paper>
                    ))
                ) : (
                    <p style={{ textAlign: "center" }}>Loading messages...</p>
                )}
            </div>

            <div style={{ backgroundColor: "#AAA9AA", height: "60px", display: "flex", alignItems: "center", justifyContent: "center" }}>
                <input type="text" style={{ width: "100%", maxWidth: "500px", padding: "10px", boxSizing: "border-box", border: "none", fontSize: "16px" }} placeholder="Type your message..." />
            </div>
        </div>
    );


}
