import React, { useState, useEffect } from 'react';
import { getMessages } from '../services/messageService';
import { Paper } from '@mui/material';


export default function Messages(props) {
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        // Fetch messages when props.noteId changes
        const fetchMessages = async () => {
            try {
                const data = await getMessages(props.noteId);
                setMessages(data);  // Assuming getMessages returns an array of messages
            } catch (error) {
                console.error('Error fetching messages:', error);
                // Optionally, handle error state or display a message to the user
            }
        };

        fetchMessages();
    }, [props.noteId]);  // Run effect whenever props.noteId changes
    console.log(messages)
    // Render messages or loading indicator
    return (
        <div>
            <h2>Messages for Note ID: {props.noteId}</h2>
            <div style={{ maxHeight: "85vh", overflow: "auto" }}>
                {messages.length > 0 ? (
                    messages.map((message, index) => (
                        <div key={index} className="comment-container" style={{ display: "flex", alignItems: "flex-start", marginBottom: "10px" }}>
                            <div className="comment" style={{ maxWidth: "500px", minWidth: "200px", border: "1px solid #ccc", backgroundColor: "#f9f9f9", padding: "10px", marginBottom: "10px", display: "flex" }}>
                                <div className="comment-content" style={{ display: "flex", flexDirection: "column" }}>
                                    <div className="comment-text" style={{ wordWrap: "break-word" }}>{message.message_content}</div>
                                    <br />
                                    {/* <div className="comment-date" style={{ marginTop: "5px", display: "flex", justifyContent: "space-between", minWidth: "200px" }}>
                                        {formattedDate} 
                                        <div className="delete-icon" style={{ marginRight: "10px", cursor: "pointer" }} onClick={() => deleteMessage(message.message_id)}>
                                            <i className="fa fa-trash"></i>
                                        </div> */}
                                    {/* </div> */}
                                </div>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>Loading messages...</p>
                )}
            </div>
        </div>
    );


}
